public class convertnegativetopositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number =-40;
		if (number<0) {
			int positivenumber=(-1)*number;
			System.out.println( "The positive number is:" +positivenumber+" ");
		
		}
	}

}
